.slider_container {
    width: 100vw;
    height: 80vh;
    
    .slider {
        position: relative;
        overflow: hidden;
        max-width: 900px;
        top: 10%;
        right: 0%;
        height: 80%;
        width: 85%;
        margin: 1.5em auto;

        &__slide {
            position: absolute;
            transform: translateY(-100%);
            left: 0;
            width: 100%;
            height: 75%;
            overflow: hidden;
            opacity: 0;
            transition: transform 0.7s ease-in-out, opacity 0.5s linear;
            background-position: center center;
            background-size: cover;

            .slider__content {
                position: absolute;
                width: 100%;
                top: 0;
                padding: 15px;
                box-sizing: border-box;
                background: rgba(0, 0, 0, 0.4);
                transition: transform 0.5s linear 0.5s;
                transform: translateX(-100%);
                color: #f3f3f3;
                border-radius: 0px 0px 50px 0px; 

                .slider__title {
                    font-size: 1em;
                    font-family: $fontTitle;
                    margin: 10px;

                    @media (min-width: $medium) {
                        font-size: 1.25em;                 
                    }
                }
                .slider__descrip {
                    font-size: 16px;
                    line-height: 1.45em;


                    @media (min-width: $medium) {
                        font-size: 14px;  
                        line-height: 1.45;                   
                    }
                }
            }
            &.active {
                opacity: 1;
                transform: translateY(0%);

                .slider__content {
                    transform: translateX(0%);
                }
            }
        }
        &__btns {
            position: absolute;
            z-index: 999;
            top: 50%;
            transform: translateY(-50%);

            width: 100%;
            padding: 15px;
            box-sizing: border-box;

            display: flex;
            justify-content: space-between;
        }
        .slider__btn {
            width: 35px;
            height: 35px;
            font-size: 20px;
            line-height: 35px;
            text-align: center;
            color: $darkBlue;
            font-weight: 300;
            border-radius: 50%;
            background: $veryLightGray;
            border: 1px solid $darkBlue;
            cursor: pointer;
            transition: background 0.2s linear, color 0.2s ease-out;

            &:hover {
                background: #ccc8c8;
                color: #171717;
            }
        }
    }
}

